const AuthRootComponent: React.FC  = (): JSX.Element => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [repeatPassword, setRepeatPassword] = useState('')

    const [secondFactor, setSecondFactor] = useState('')
    const [open, setOpen] = useState(false)
    const location = useLocation()

    const dispatch = useAppDispatch()
    const navigate = useNavigate()

    const handleSubmit = async (event: { preventDefault: () => void; }) => {
        event.preventDefault()

        if (location.pathname === '/login') {
            try {
                const userData = {
                    email,
                    password,
                }
                const user = await instance.post('/login/', userData)
                dispatch(login(user.data))
                navigate('/')
            } 
            catch (e) {
                return e
            }
        }
        else {
            if (password === repeatPassword) {
                try {
                    const userData = {
                        email,
                        password
                    }
                    const newUser = await instance.post('/register/', userData)
                    dispatch(login(newUser.data))
                    navigate('/')
                }
                catch (e) {
                    return e
                }
            }
            else {
                throw new Error('Пароли не совпадают')
            }
        }
    }

    const handleClose = () => {
        setOpen(false);
      };

    return (
        <div className='root'>
            <form className='form' onSubmit={handleSubmit}>
                <Box
                    display='flex'
                    justifyContent='center'
                    alignItems='center'
                    flexDirection='column'
                    maxWidth={640}
                    margin='auto'
                    padding={5}
                    borderRadius={5}
                    boxShadow={'5px 5px 10px #ccc'}
                >
                    {
                    (location.pathname === '/login' 
                        ? <LoginPage setEmail={setEmail} setPassword={setPassword} setSecondFactor={setSecondFactor} navigate={navigate}/> : location.pathname === '/register' 
                            ? <RegisterPage setEmail={setEmail} setPassword={setPassword} setRepeatPassword={setRepeatPassword}/> : null)}
                </Box>
                <Dialog open={open} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                        To subscribe to this website, please enter your email address here. We will send updates
                        occasionally.
                        </DialogContentText>
                        <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="2FA Code"
                        type="tel"
                        fullWidth
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose} color="primary">
                        Cancel
                        </Button>
                        <Button onClick={handleClose} color="primary">
                        Subscribe
                        </Button>
                    </DialogActions>
                </Dialog>
            </form>
        </div>
    )
}

export default AuthRootComponent;
