const AuthRootComponent = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [secondFactor, setSecondFactor] = useState('')
    const [open, setOpen] = useState(false)
    const location = useLocation()

    const handleSubmit = async (event: { preventDefault: () => void; }) => {
        event.preventDefault()

        const userData = {
            email,
            password
        }

        const user = await axios.post('http://127.0.0.1:8000/register/', userData)
        console.log(user.data)
        setOpen(true);

        console.log(email)
        console.log(password)
        console.log(secondFactor)
    }

    const handleClose = () => {
        setOpen(false);
      };

    return (
        <div className='root'>
            <img src={logo}>
            <form className='form' onSubmit={handleSubmit}>
                <Box
                    display='flex'
                    justifyContent='center'
                    alignItems='center'
                    flexDirection='column'
                    maxWidth={640}
                    margin='auto'
                    padding={5}
                    borderRadius={5}
                    boxShadow={'5px 5px 10px #ccc'}
                >
                    {(location.pathname === '/login' ? <LoginPage setEmail={setEmail} setPassword={setPassword} setSecondFactor={setSecondFactor}/> : location.pathname === '/register' ? <RegisterPage /> : null)}
                </Box>
                <Dialog open={open} aria-labelledby="form-dialog-title">
                    <img src={logo} />
                    <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                        To subscribe to this website, please enter your email address here. We will send updates
                        occasionally.
                        </DialogContentText>
                        <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Email Address"
                        type="email"
                        fullWidth
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose} color="primary">
                        Cancel
                        </Button>
                        <Button onClick={handleClose} color="primary">
                        Subscribe
                        </Button>
                    </DialogActions>
                </Dialog>
            </form>
            </img>
        </div>
    )
}

export default AuthRootComponent;
