import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import LoginPage from './login';
import RegisterPage from './register';
import Box from '@mui/material/Box';
import './style.css'
import axios from 'axios';

const AuthRootComponent = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [secondFactor, setSecondFactor] = useState('')
    const location = useLocation()

    const handleSubmit = async (event: { preventDefault: () => void; }) => {
        event.preventDefault()

        const userData = {
            email,
            password
        }

        const user = await axios.post('http://127.0.0.1:8000/register/', userData)
        console.log(user.data)

        console.log(email)
        console.log(password)
        console.log(secondFactor)
    }

    return (
        <div className='root'>
            <form className='form' onSubmit={handleSubmit}>
                <Box
                    display='flex'
                    justifyContent='center'
                    alignItems='center'
                    flexDirection='column'
                    maxWidth={640}
                    margin='auto'
                    padding={5}
                    borderRadius={5}
                    boxShadow={'5px 5px 10px #ccc'}
                >
                    {(location.pathname === '/login' ? <LoginPage setEmail={setEmail} setPassword={setPassword} setSecondFactor={setSecondFactor}/> : location.pathname === '/register' ? <RegisterPage /> : null)}
                </Box>
            </form>
        </div>
    )
}

export default AuthRootComponent;
